    public function getSourceContext()
    {
        return new Source("{# {% extends 'base.html.twig' %} #}


{% block title %}Log in!{% endblock %}

{% block body %}
<body>
    <header>
        <h2 class=\"logo\"> SeMi </h2>
            <nav class=\"navigation\">
                
            </nav>
    </header>
</body>
<div class=\"container\">
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
        </div>
    {% endif %}


    <h1 class=\"st\">Please Sign In</h1><hr>
    <label for=\"inputUsername\">Username</label>
    <input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" class=\"form-control\" autocomplete=\"username\" required autofocus><br>
    <label for=\"inputPassword\">Password</label>
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >
<hr>
    <button class=\"btn btn-danger\" type=\"submit\">
        Sign in
    </button>
    <div class=\"login-register\"> 
        <p>Don't have an account?  
            <a href=\"{{ path('app_register') }}\" class=\"register-link\"> Register </a> 
            
   
    </div>
</form>
</div>
{% endblock %}
{% block stylesheets %}
<link rel=\"stylesheet\" href=\"{{asset('css/bootstrap.css')}}\"/>
<link rel=\"stylesheet\" href=\"{{asset('cssK/Login.css')}}\"/>
{% endblock %}", "security/login.html.twig", "E:\\BTEC\\Semi\\templates\\security\\login.html.twig");
